using FluentAssertions;
using Nethermind.Core.Extensions;
using Nethermind.Crypto;
using NUnit.Framework;

namespace Nethermind.Core.Test
{
    public class Blake2Tests
    {
        [Test]
        [TestCase("", "08c9bcf367e6096a3ba7ca8485ae67bb2bf894fe72f36e3cf1361d5f3af54fa5d282e6ad7f520e511f6c3e2b8c68059b9442be0454267ce079217e1319cde05b")]
        [TestCase("", "ba80a53f981c4d0d6a2797b69f12f6e94c212f14685ac4b74b12bb6fdbffa2d17d87c5392aab792dc252d5de4533cc9518d38aa8dbf1925ab92386edd4009923")]
        [TestCase("", "75ab69d3190a562c51aef8d88f1c2775876944407270c42c9844252c26d2875298743e7f6d5ea2f2d3e8d226039cd31b4e426ac4f2d3d666a610c2116fde4735")]
        [TestCase("", "b63a380cb2897d521994a85234ee2c181b5f844d2c624c002677e9703449d2fba551b3a8333bcdf5f2f7e08993d53923de3d64fcc68c034e717b9293fed7a421")]
//        [TestCase("", "fc59093aafa9ab43daae0e914c57635c5402d8e3d2130eb9b3cc181de7f0ecf9b22bf99a7815ce16419e200e01846e6b5df8cc7703041bbceb571de6631d2615")]
        public void compression_function_should_return_valid_output(string input, string output)
        {
            var blake2Optimized = new Blake2Compression();
            byte[] blake2Result = new byte[64];
            blake2Optimized.Compress(Bytes.FromHexString(input), blake2Result);
            var result = blake2Result.ToHexString();
            result.Should().BeEquivalentTo(output);
        }
    }
}
