using Nethermind.Abi;
using Nethermind.Core;

namespace Nethermind.DataMarketplace.Core.Services.Models
{
    public static class ContractData
    {
        public static string InitCode =
        "#owner#00000000000000000000000000000000000000000000000000000000000007d0";
//        "#owner#00000000000000000000000000000000000000000000000000000000000007d0";

        public static string DeployedCode = @"";

        public static string GetInitCode(Address ownerAndFees)
        {
            return InitCode.Replace("#owner#", ownerAndFees.ToString(false, false));
        }
        
        public static readonly AbiSignature ClaimPaymentAbiSig = new AbiSignature("claimPayment",
                    new AbiBytes(32),
                    new AbiUInt(32),
                    new AbiUInt(96),
                    new AbiUInt(32),
                    new AbiBytes(16),
                    AbiType.Address,
                    AbiType.Address,
                    new AbiFixedLengthArray(new AbiUInt(32), 2),
                    new AbiUInt(8),
                    new AbiBytes(32),
                    new AbiBytes(32));
        
        public static AbiSignature DepositAbiSig = new AbiSignature("deposit", new AbiBytes(32), new AbiUInt(32), new AbiUInt(32));
        public static AbiSignature DepositBalanceAbiSig = new AbiSignature("getCurrentBalance", new AbiBytes(32));
        public static AbiSignature VerifyDepositAbiSig = new AbiSignature("verifyDeposit", new AbiBytes(32));
        
        public static AbiSignature ClaimRefundSig = new AbiSignature("claimRefund",
            new AbiBytes(32), // assetID
            new AbiUInt(32), // units
            new AbiUInt(96), // value
            new AbiUInt(32), // expiryTimestamp
            new AbiBytes(16), // pepper
            AbiType.Address, // providerHotAddress
            AbiType.Address); // refundTo
        
        public static AbiSignature ClaimEarlyRefundSig = new AbiSignature("claimEarlyRefund",
            new AbiBytes(32), // assetID
            new AbiUInt(32), // units
            new AbiUInt(96), // value
            new AbiUInt(32), // expiryTimestamp
            new AbiBytes(16), // pepper
            AbiType.Address, // providerHotAddress
            new AbiUInt(32), // claimableAfter
            new AbiUInt(8), // v
            new AbiBytes(32), // r
            new AbiBytes(32), // s
            AbiType.Address); // payable refundTo
    }
}