using System;
using System.Collections.Generic;
using System.Linq;
using System.Threading;
using System.Threading.Tasks;
using Nethermind.Core;
using Nethermind.Core.Collections;
using Nethermind.Core.Crypto;
using Nethermind.Core.Extensions;
using Nethermind.Core.Test.Builders;
using Nethermind.Crypto;
using Nethermind.Evm;
using Nethermind.Evm.Precompiles;
using Nethermind.Facade.Proxy.Models;
using Nethermind.Facade.Proxy.Models.Simulate;
using Nethermind.Facade.Simulate;
using NUnit.Framework;

namespace Nethermind.JsonRpc.Test.Modules.Eth;

public class EthSimulateTestsSimplePrecompiles : EthRpcSimulateTestsBase
{
    /* Compiled contract
    * Call example for TestItem.AddressA
    * recover 0xb6e16d27ac5ab427a7f68900ac5559ce272dc6c37c82b3e052246c82244c50e4 28 0x7b8b1991eb44757bc688016d27940df8fb971d7c87f77a6bc4e938e3202c4403 0x7e9267b0aeaa82fa765361918f2d8abd9cdd86e64aa6f2b81d3c4e0b69a7b055
    * returns address: 0xb7705aE4c6F81B66cdB323C65f4E8133690fC099

    pragma solidity ^0.8.7;

    contract EcrecoverProxy {
        function recover(bytes32 hash, uint8 v, bytes32 r, bytes32 s) public pure returns (address) {
                return ecrecover(hash, v, r, s);
        }
    }
    */

    //Taken from contract compiler output metadata
    public const string EcRecoverCallerContractBytecode =
        "";


    /// <summary>
    ///     This test verifies that a temporary forked blockchain can updates precompiles
    /// </summary>
    [Test]
    public async Task Test_eth_simulate_erc()
    {
        TestRpcBlockchain chain = await CreateChain();

        //Impose Opcode instead of EcRecoverPrecompile, it returns const TestItem.AddressE address
        byte[] code = Prepare.EvmCode
            .StoreDataInMemory(0, TestItem.AddressE
                .ToString(false, false)
                .PadLeft(64, '0'))
            .PushData(Bytes.FromHexString("0x20"))
            .PushData(Bytes.FromHexString("0x0"))
            .Op(Instruction.RETURN).Done;

        // Step 1: Hash the message
        Hash256 messageHash = Keccak.Compute("Hello, world!");
        // Step 2: Sign the hash
        Signature signature = chain.EthereumEcdsa.Sign(TestItem.PrivateKeyA, messageHash);

        Address contractAddress = await DeployEcRecoverContract(chain, TestItem.PrivateKeyB, EcRecoverCallerContractBytecode);
        byte[] transactionData = GenerateTransactionDataForEcRecover(messageHash, signature);

        SystemTransaction tx = new()
        {
            Data = transactionData,
            To = contractAddress,
            SenderAddress = TestItem.PublicKeyB.Address
        };
        tx.Hash = tx.CalculateHash();

        TransactionWithSourceDetails txDetails = new()
        {
            Transaction = tx,
            HadGasLimitInRequest = false,
            HadNonceInRequest = false
        };

        SimulatePayload<TransactionWithSourceDetails> payload = new()
        {
            BlockStateCalls =
            [
                new BlockStateCall<TransactionWithSourceDetails>
                {
                    StateOverrides = new Dictionary<Address, AccountOverride>
                    {
                        { EcRecoverPrecompile.Address, new AccountOverride { Code = code } }
                    },
                    Calls = [txDetails]
                }
            ],
            TraceTransfers = true
        };

        SimulateOutput result = chain.Bridge.Simulate(chain.BlockFinder.Head?.Header!, payload, CancellationToken.None);

        using ArrayPoolList<byte> addressBytes = result.Items[0].Calls[0].ReturnData!.SliceWithZeroPaddingEmptyOnError(12, 20);
        Address resultingAddress = new(addressBytes.ToArray());
        Assert.That(resultingAddress, Is.EqualTo(TestItem.AddressE));

        //Check that initial VM is intact
        Address? mainChainRpcAddress = EcRecoverCall(chain, TestItem.AddressB, transactionData, contractAddress);
        Assert.That(mainChainRpcAddress, Is.EqualTo(TestItem.AddressA));
    }
}
